package operadorsrelacionals;

public class OperadorsRelacionals {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 5;
		int b = 5;
		
		boolean resultat = a == b;
		System.out.println(resultat);
		
		resultat = a != b;
		System.out.println(resultat);
		
		resultat = a > b;
		System.out.println(resultat);
		
		resultat = a >= b;
		System.out.println(resultat);
		
		resultat = a < b;
		System.out.println(resultat);
		
		resultat = a <= b;
		System.out.println(resultat);
		
	}

}
